presets.template:
; Font: {{name}} ({{definedCount}} chars, size={{lastDefinedIndex}})
{{#each characters}}{{#if (lte index ../lastDefinedIndex)}}	DEFW	#{{hex segments}}	; {{index}} {{#if name}}({{name}}){{/if}}
{{/if}}{{/each}}

#ifndef FONT_{{nameUpper}}_H
#define FONT_{{nameUpper}}_H

#include <stdint.h>

// Font: {{name}}
static const uint16_t font_{{nameLower}}[256] = {
{{#each characters}}    0x{{hex segments}},  // {{index}}: {{#if defined}}{{name}}{{else}}(empty){{/if}}
{{/each}} };

#endif // FONT_{{nameUpper}}_H

#ifndef FONT_{{nameUpper}}_H
#define FONT_{{nameUpper}}_H

#include <stdint.h>

// Font: {{name}} ({{definedCount}} chars)
#define FONT_{{nameUpper}}_SIZE {{lastDefinedIndex}}

static const uint16_t font_{{nameLower}}[{{lastDefinedIndex}}] = {
{{#each characters}}{{#if (lte index ../lastDefinedIndex)}}    0x{{hex segments}},  // {{index}}: {{#if defined}}{{name}}{{else}}(empty){{/if}}
{{/if}}{{/each}} };

#endif // FONT_{{nameUpper}}_H
